import revenueRepository from '../revenueRepository';
import { supabase } from '../../services/supabase';

// Mock do Supabase
jest.mock('../../services/supabase', () => ({
  supabase: {
    from: jest.fn(() => ({
      insert: jest.fn(() => ({
        select: jest.fn(() => ({
          single: jest.fn()
        }))
      })),
      select: jest.fn(() => ({
        eq: jest.fn(() => ({
          single: jest.fn()
        }))
      }))
    }))
  }
}));

describe('RevenueRepository', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  describe('create()', () => {
    it('should create revenue successfully', async () => {
      const mockData = {
        type: 'service',
        value: 150.00,
        date: '2025-10-18',
        unit_id: 'a1b2c3d4-e5f6-7890-abcd-ef1234567890'
      };

      const mockResponse = {
        id: 'new-id',
        ...mockData,
        created_at: '2025-10-18T14:30:00.000Z'
      };

      (supabase.from as jest.Mock).mockReturnValue({
        insert: jest.fn().mockReturnValue({
          select: jest.fn().mockReturnValue({
            single: jest.fn().mockResolvedValue({
              data: mockResponse,
              error: null
            })
          })
        })
      });

      const result = await revenueRepository.create(mockData);

      expect(result.data).toEqual(mockResponse);
      expect(result.error).toBeNull();
    });

    it('should handle database errors', async () => {
      const mockData = {
        type: 'service',
        value: 150.00,
        date: '2025-10-18'
      };

      (supabase.from as jest.Mock).mockReturnValue({
        insert: jest.fn().mockReturnValue({
          select: jest.fn().mockReturnValue({
            single: jest.fn().mockResolvedValue({
              data: null,
              error: { code: '23503', message: 'Foreign key violation' }
            })
          })
        })
      });

      const result = await revenueRepository.create(mockData);

      expect(result.data).toBeNull();
      expect(result.error).toContain('ReferÃªncia invÃ¡lida');
    });

    it('should sanitize forbidden fields', async () => {
      const mockData = {
        type: 'service',
        value: 150.00,
        date: '2025-10-18',
        id: 'should-be-removed',
        profit: 'should-be-removed'
      };

      (supabase.from as jest.Mock).mockReturnValue({
        insert: jest.fn().mockReturnValue({
          select: jest.fn().mockReturnValue({
            single: jest.fn().mockResolvedValue({
              data: { id: 'new-id' },
              error: null
            })
          })
        })
      });

      await revenueRepository.create(mockData);

      const insertCall = (supabase.from as jest.Mock).mock.results[0].value.insert;
      const insertedData = insertCall.mock.calls[0][0];

      expect(insertedData).not.toHaveProperty('id');
      expect(insertedData).not.toHaveProperty('profit');
    });
  });

  describe('findAll()', () => {
    it('should fetch revenues with filters', async () => {
      const mockRevenues = [
        { id: '1', value: 100, type: 'service' },
        { id: '2', value: 200, type: 'product' }
      ];

      (supabase.from as jest.Mock).mockReturnValue({
        select: jest.fn().mockReturnValue({
          eq: jest.fn().mockReturnValue({
            gte: jest.fn().mockReturnValue({
              lte: jest.fn().mockReturnValue({
                order: jest.fn().mockReturnValue({
                  range: jest.fn().mockResolvedValue({
                    data: mockRevenues,
                    error: null,
                    count: 2
                  })
                })
              })
            })
          })
        })
      });

      const result = await revenueRepository.findAll(
        { unit_id: 'abc123', start_date: '2025-10-01', end_date: '2025-10-31' },
        { page: 1, limit: 50 }
      );

      expect(result.data).toEqual(mockRevenues);
      expect(result.count).toBe(2);
      expect(result.error).toBeNull();
    });
  });
