### **3. Testes de Service**

**Arquivo:** `src/services/__tests__/financeiroService.spec.ts`

```typescript
import financeiroService from '../financeiroService';
import revenueRepository from '../../repositories/revenueRepository';
import { CreateRevenueDTO } from '../../dtos/revenueDTO';

jest.mock('../../repositories/revenueRepository');

describe('FinanceiroService', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  describe('createReceita()', () => {
    it('should create receita with valid data', async () => {
      const inputData = {
        type: 'service',
        value: 150.00,
        date: '2025-10-18',
        unit_id: 'abc123'
      };

      const mockDbResponse = {
        id: 'new-id',
        ...inputData,
        status: 'Pending',
        created_at: '2025-10-18T14:30:00.000Z'
      };

      (revenueRepository.create as jest.Mock).mockResolvedValue({
        data: mockDbResponse,
        error: null
      });

      const result = await financeiroService.createReceita(inputData);

      expect(result.error).toBeNull();
      expect(result.data).toHaveProperty('id', 'new-id');
      expect(result.data.value).toBe(150.00);
    });

    it('should reject invalid data before calling repository', async () => {
      const invalidData = {
        type: 'service',
        value: -100, // Valor negativo
        date: '2025-10-18'
      };

      const result = await financeiroService.createReceita(invalidData);

      expect(result.error).toContain('maior que zero');
      expect(result.data).toBeNull();
      expect(revenueRepository.create).not.toHaveBeenCalled();
    });

    it('should block forbidden fields', async () => {
      const dataWithForbidden = {
        type: 'service',
        value: 150.00,
        date: '2025-10-18',
        valor: 'forbidden-field'
      };

      const result = await financeiroService.createReceita(dataWithForbidden);

      expect(result.error).toContain('campos em portuguÃªs detectados');
      expect(revenueRepository.create).not.toHaveBeenCalled();
    });
  });
});
